import React, { useState } from "react";

const useInput = (initialValue = null) => {
  const [value, setValue] = useState(initialValue);
  const handler = useCallback(
    (e) => setValue(e.target.value),
    [value, handler]
  );
  return [value, handler];
};

export default useInput;
