const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  },
};

//action creator
const changeNickname = (data) => {
  return { type: "CHANGE_NICKNAME", data: "boogiYang" };
};

// changeNickname("boogiYang");

// (이전 상태 , 액션) => 다음 상태
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "CHANGE_NICKNAME":
      return {
        ...state,
        name: action.data,
      };
  }
};

export default rootReducer;
