import React, { useState, useCallback, useMemo, userRef } from "react";
import { Form, Input, Button } from "antd";
import { useSelector, useDispatch } from "react-redux";
import { addPost } from "../reducers/post";
const PostForm = () => {
  const { imagePaths } = useSelector((state) => state.post);
  const dispatch = useDispatch();
  const formStyle = useMemo(() => {
    margin: "10px 0 20px";
  }, []);
  const submitButtonStyle = useMemo(() => {
    float: "right";
  }, []);
  const [text, setText] = useState("");
  const onChangeText = useCallback((e) => {
    const value = e.target.value;
    setText(value);
  });
  const onSubmit = useCallback(() => {
    dispatch(addPost({}));
  }, []);

  const imageInput = userRef(null);
  return (
    <Form style={formStyle} encType="multipart/form-data" onFinish={onSubmit}>
      <Input.TextArea
        value={text}
        onChange={onChangeText}
        maxLength={140}
        placeholder="어떤 신기한 일이 있었나요?"
      />
      <div>
        <input id="fileButton" type="file" multiple hidden ref={imageInput} />
        <Button htmlFor="fileButton">이미지 업로드</Button>
        <Button type="primary" style={submitButtonStyle} htmlType="submit">
          짹짹
        </Button>
        <div>
          {imagePaths.map((v) => {
            return (
              <div key={v} style={{ display: "inline-block" }}>
                <img src={v} style={{ width: "200px" }} />
                <div>
                  <Button>제거</Button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </Form>
  );
};

export default PostForm;
